package exam;

public class Q2739 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N = 2;
		int a = 1;
		while(a < 10) {
			System.out.println(N + " * " + a + " = " + N * a);
			a++;
		}
		
		System.out.println();
		
		for(int i = 1; i < 10; i++) {
			System.out.println("2 " + "* " + i + " = " + 2 * i);
		}
	}

}
