package java_exam;

public class ArrayExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] scoreArray = new int[7]; // 자바가 지원하는 공식문법
		System.out.println(scoreArray);
		//결과 : [I@6b95977 읽는 법 ==> 엣(@)을 기준으로 짜름.
		// [I <== 변수의 타입 / 6b95977 <== hashcode / hashdata ==> 변수의 값을 암호화 시킨 값
		// [ <== 배열이라는 뜻 / I <== 타입(Integer)
		
		int score = 60;
		System.out.println(score);
		
//		int scoreArray2[] = new int[7];// C언어에서 지원하는 문법
	}

}
